javascript:(function(){const links=Array.from(document.querySelectorAll('a'));const downloadUrls=[];const seen=new Set();links.forEach(a=>{const href=a.href||"";const text=(a.innerText||"").toUpperCase().trim();const isHumbleCdn=href.includes('cdn.humble.com');const isZipText=text==='ZIP';const isDownloadText=text==='HERUNTERLADEN'||text==='DOWNLOAD';if(href&&(isHumbleCdn||isZipText||isDownloadText)&&!seen.has(href)){if(href.startsWith('http')){seen.add(href);downloadUrls.push(href);}}});if(downloadUrls.length===0){alert("Keine Download-Links gefunden!");return;}
if(!confirm(downloadUrls.length+" Downloads gefunden. Möchtest du den Bulk-Download starten?\n\n(Hinweis: Erlaube Pop-ups für diese Seite, falls der Browser blockiert.)")){return;}
downloadUrls.forEach((url,index)=>{setTimeout(()=>{console.log("Starte Download "+(index+1)+": "+url);const iframe=document.createElement('iframe');iframe.style.display='none';iframe.src=url;document.body.appendChild(iframe);setTimeout(()=>document.body.removeChild(iframe),30000);},index*1500);});alert("Bulk-Download gestartet. Bitte lass den Tab offen, bis alle Dateien angefordert wurden.");})();