javascript:(function(){const links=Array.from(document.links);const results=[];const seen=new Set();links.forEach(link=>{const url=link.href;const text=link.innerText.trim()||link.title||"Kein Text";if(url.startsWith('http')&&!seen.has(url)){seen.add(url);results.push({text:text,url:url,page:document.title,timestamp:new Date().toISOString()});}});if(results.length===0){alert("Keine Links gefunden!");return;}
const dataStr=JSON.stringify(results,null,2);const blob=new Blob([dataStr],{type:"application/json"});const url=URL.createObjectURL(blob);const a=document.createElement('a');const ts=new Date().toISOString().replace(/[:.]/g,'-').slice(0,16);a.href=url;a.download=`${ts}_links_export.json`;document.body.appendChild(a);a.click();document.body.removeChild(a);alert(`${results.length}Links wurden als JSON exportiert!`);})();