javascript:(function(){var i='qr-bm-ol';if(document.getElementById(i))return;var s=document.createElement('script');s.src='https://cdnjs.cloudflare.com/ajax/libs/qrcodejs/1.0.0/qrcode.min.js';s.onerror=function(){alert("Sicherheitsblockade (CSP): Diese Seite erlaubt das Laden externer Scripte nicht. Das Bookmarklet kann hier leider nicht ausgeführt werden.");};s.onload=function(){var o=document.createElement('div');o.id=i;o.style.cssText='position:fixed;top:0;left:0;width:100%;height:100%;background:rgba(0,0,0,0.7);z-index:100000;display:flex;justify-content:center;align-items:center;';var b=document.createElement('div');b.style.cssText='background:#fff;padding:25px;border-radius:10px;text-align:center;position:relative;font-family:sans-serif;';var c=document.createElement('span');c.innerHTML='×';c.style.cssText='position:absolute;top:0;right:10px;cursor:pointer;font-size:28px;color:#999;';c.onclick=function(){document.body.removeChild(o);};var q=document.createElement('div');q.style.margin='15px auto';var d=document.createElement('a');d.innerText='Bild speichern';d.style.cssText='display:inline-block;margin-top:15px;padding:10px 20px;background:#333;color:white;text-decoration:none;border-radius:5px;';b.appendChild(c);b.appendChild(q);b.appendChild(d);o.appendChild(b);document.body.appendChild(o);new QRCode(q,{text:window.location.href,width:220,height:220});setTimeout(function(){var cv=q.getElementsByTagName('canvas')[0];d.href=cv?cv.toDataURL():q.getElementsByTagName('img')[0].src;d.download='qr.png';},200);};document.body.appendChild(s);})();